import React, {FC} from 'react';
import Toe, {Type} from "./Toe";

export interface GameProps {
    width?: string,
    height?: string
}

var GameState = [
    [Type.type_o], [Type.none], [Type.none],
    [Type.none], [Type.none], [Type.none],
    [Type.none], [Type.none], [Type.none]
]
const Game: FC<GameProps> = (
    {
        width = '300px',
        height = '300px'
    }) => {
    return (
        <div
            style={{
                width,
                height,
                display: 'flex',
                flexWrap: 'wrap',
                alignItems: 'center',
                justifyContent: 'space-around'

            }}
        >
            <Toe onClick={() => {
                ToeOnClick(0,0)
            }}
                 type={GetElementState(0, 0)}
            />
            <Toe onClick={() => {
                ToeOnClick(0,1)
            }}
                 type={GetElementState(0, 1)}
            />
            <Toe onClick={() => {
                ToeOnClick(0,2)
            }}
                 type={GetElementState(0, 2)}
            />
            <Toe onClick={() => {
                ToeOnClick(1,0)
            }}
                 type={GetElementState(1, 0)}
            />
            <Toe onClick={() => {
                ToeOnClick(1,1)
            }}
                 type={GetElementState(1, 1)}
            />
            <Toe onClick={() => {
                ToeOnClick(1,2)
            }}
                 type={GetElementState(1, 2)}
            />
            <Toe onClick={() => {
                ToeOnClick(2,0)
            }}
                 type={GetElementState(2, 0)}
            />
            <Toe onClick={() => {
                ToeOnClick(2,1)
            }}
                 type={GetElementState(2, 1)}
            />
            <Toe onClick={() => {
                ToeOnClick(2,2)
            }}
                 type={GetElementState(2, 2)}
            />

        </div>
    );

};
const GetElementState = (x: number, y: number) => {
    return GameState[x][y];
}
const ToeOnClick = (x:number, y:number) => {
    if(GameState[x][y]==Type.none){
        GameState[x][y]=Type.type_x;
    }
    
}

export default Game;
