import React, {FC, useState} from 'react';
import x_img from '../res/x.png';
import round_img from '../res/round.png';

export enum Type {
    type_x = 'type_x',
    type_o = 'type_o',
    none = 'none'
}

export interface ToeProps {
    type?: Type | undefined,
    onClick: (state:any) => void
}

//func
const ConvertType = (value: Type) => {
    if (value == Type.type_x) return x_img;
    else if (value == Type.type_o) return round_img;
    else if (value == undefined) return null;
    else return null;
}
//component
const Toe: FC<ToeProps> = ({onClick,type}) => {
    const [state, setState] = useState({
        type: type

    });
    if (state.type == Type.none) return (
        <div style={{
            border: '2px solid black',
            borderRadius: '10px',
            display: 'flex',
            width: '90px',
            height: '90px',
            alignSelf: 'center',
            verticalAlign: 'baseline',
            flexFlow: 'row wrap',
            flexWrap: 'wrap'
        }}
             onClick={(state)=>onClick}
        />
    )
    else return (
        <div style={{
            border: '2px solid black',
            borderRadius: '10px',
            display: 'flex',
            width: '90px',
            height: '90px',
            alignSelf: 'center',
            verticalAlign: 'baseline',
            flexFlow: 'row wrap',
            flexWrap: 'wrap'
        }}
             onClick={(state)=>onClick}
        >
            <img

                style={{width: '90px', height: '90px'}}
                src={(state.type == null ? ' ' : ConvertType(state.type))?.toString()}
                alt={state.type?.toString()}
            />

        </div>
    );
};

export default Toe;
