import React, {FC} from 'react';
export enum TicTacState {
    state_x='res/x.png',
    state_o='res/round.png',
    empty = 'empty'
}
export  interface TicTacItemProps{
    onClick:()=>void
}
const TicTacItem:FC<TicTacItemProps> = () => {
    var currentState:TicTacState =TicTacState.empty;
    const ChangeState = () => {
      if(currentState==TicTacState.empty) currentState = TicTacState.state_o;
      else currentState = TicTacState.empty;
    }

    if(currentState == TicTacState.empty){
        return(
            <div
                onClick={()=>{
                    ChangeState();
                }}>

            </div>
        );
    }
    else {
        return (
            <div
                onClick={()=>{
                    ChangeState();
                }}
            >
                <img src={currentState}></img>
            </div>
        );
    }
};

export default TicTacItem;
