import React, {FC, useState} from 'react';
import {IconButton} from "@mui/material";
import Toe, {Type} from "./Toe";
import Winner from './Winner'

export interface GameProps {
    width?: string,
    height?: string
}


const GameStatement: Type[][] = [
    [Type.none, Type.none, Type.none],
    [Type.none, Type.none, Type.none],
    [Type.none, Type.none, Type.none]
]

enum Turn {
    player1 = 'player1',
    player2 = 'player2'
}

const Game: FC<GameProps> = (
    {
        width = '400px',
        height = '400px'
    }) => {

    let curTurn: Turn = Turn.player1;
    let continueFlag = true;
    let winner:string = 'none';

    function GetGameField(x: number, y: number) {
        if (curTurn === Turn.player1) {
            GameStatement[x][y] = Type.type_o;
        } else {
            GameStatement[x][y] = Type.type_x;
        }
        // eslint-disable-next-line no-restricted-globals
        curTurn = SwitchTurn(curTurn);
        if (EndGameCheck().EndGame) {
            WinnerMessage(EndGameCheck().Winner)
            winner=EndGameCheck().Winner.toString();
        }
        if (!continueFlag) return Type.none;
        else return GameStatement[x][y];
    }

    const WinnerMessage = (winner: Type) => {
        setTimeout(() => {
            continueFlag = false
        }, 10);
        alert('WINNER IS' + winner.toString());
    }


    const ThatPlayerWon = (toeType: Type) => {
        for (let i = 0; i < 3; i++) {
            if ((GameStatement[i][0] === toeType) &&
                (GameStatement[i][1] === toeType) &&
                (GameStatement[i][2] === toeType)) {
                return true;
            }
        }
        for (let i = 0; i < 3; i++) {
            if ((GameStatement[0][i] === toeType) &&
                (GameStatement[1][i] === toeType) &&
                (GameStatement[2][i] === toeType)) {
                return true;
            }
        }
        if ((GameStatement[0][0] === toeType) &&
            (GameStatement[1][1] === toeType) &&
            (GameStatement[2][2] === toeType)) {
            return true;
        }
        if ((GameStatement[0][2] === toeType) &&
            (GameStatement[1][1] === toeType) &&
            (GameStatement[2][0] === toeType)) {
            return true;
        }
    }
    const IsDraw = () => {
        let NotEmptyElements = 0;
        for (let i = 0; i < 3; i++) {
            for (let j = 0; j < 3; j++) {
                if (GameStatement[i][j] !== Type.none) NotEmptyElements++;
            }
        }
        return NotEmptyElements === 9;
    }

    function EndGameCheck() {
        if (ThatPlayerWon(Type.type_o)) return {
            EndGame: true,
            Winner: Type.type_o
        }
        if (ThatPlayerWon(Type.type_x)) return {
            EndGame: true,
            Winner: Type.type_x
        }
        if (IsDraw())

            return {
                EndGame: true,
                Winner: Type.none
            };


        else return {
            EndGame: false,
            Winner: Type.none
        };
    }

    const SwitchTurn = (current: Turn) => {
        if (current === Turn.player1) return Turn.player2;
        else return Turn.player1;
    }
    return (
        <div
            style={{
                width,
                height,
                display: 'flex',
                flexWrap: 'wrap',
                alignItems: 'center',
                justifyContent: 'space-around'

            }}
        >
            <IconButton color={'primary'}
            ><Toe onClick={() => GetGameField(0, 0)}/></IconButton>
            <IconButton color={'primary'}
            ><Toe onClick={() => GetGameField(0, 1)}/></IconButton>
            <IconButton color={'primary'}
            ><Toe onClick={() => GetGameField(0, 2)}/></IconButton>
            <IconButton color={'primary'}
            ><Toe onClick={() => GetGameField(1, 0)}/></IconButton>
            <IconButton color={'primary'}
            ><Toe onClick={() => GetGameField(1, 1)}/></IconButton>
            <IconButton color={'primary'}
            ><Toe onClick={() => GetGameField(1, 2)}/></IconButton>
            <IconButton color={'primary'}
            ><Toe onClick={() => GetGameField(2, 0)}/></IconButton>
            <IconButton color={'primary'}
            ><Toe onClick={() => GetGameField(2, 1)}/></IconButton>
            <IconButton color={'primary'}
            ><Toe onClick={() => GetGameField(2, 2)}/></IconButton>
            <Winner initialiseWinner={(winner)=>{}}/>
        </div>

    );

};


export default Game;
