import React, {FC} from 'react';
import Toe, {Type} from "./Toe";

export interface GameProps {
    width?: string,
    height?: string
}

var GameStatement: Type[][] = [
    [Type.none, Type.none, Type.none],
    [Type.none, Type.none, Type.none],
    [Type.none, Type.none, Type.none]
]

enum Turn {
    player1 = 'player1',
    player2 = 'player2'
}

const Game: FC<GameProps> = (
    {
        width = '300px',
        height = '300px'
    }) => {
    return (
        <div
            style={{
                width,
                height,
                display: 'flex',
                flexWrap: 'wrap',
                alignItems: 'center',
                justifyContent: 'space-around'

            }}
        >
            <Toe onClick={() => Game(GameStatement[0][0],curTurn)}/>
            <Toe onClick={() => Game(GameStatement[0][1],curTurn)}/>
            <Toe onClick={() => Game(GameStatement[0][2],curTurn)}/>
            <Toe onClick={() => Game(GameStatement[1][0],curTurn)}/>
            <Toe onClick={() => Game(GameStatement[1][1],curTurn)}/>
            <Toe onClick={() => Game(GameStatement[1][2],curTurn)}/>
            <Toe onClick={() => Game(GameStatement[2][0],curTurn)}/>
            <Toe onClick={() => Game(GameStatement[2][1],curTurn)}/>
            <Toe onClick={() => Game(GameStatement[2][2],curTurn)}/>
        </div>
    );

};
var curType: Type = Type.type_o;
var curTurn: Turn = Turn.player1;

function Get(type: Type, turn: Turn) {

    return type;
}
const SwitchTurn = (current:Turn)=>{
    if(current==Turn.player1)return Turn.player2;
    else return Turn.player1;
}
const SwitchType = (e: Type | undefined) => {
    console.log('SwitchType working');
    if (e == undefined) console.log('e is undefined');
    if (e == Type.none) {
        e = Type.type_o;
        curType = Type.type_o
    } else if (e == Type.type_o) {
        e = Type.type_x;
        curType = Type.type_x
    } else {
        e = Type.type_o;
        curType = Type.type_o;
    }
}

const TypeInit = (e: Type | undefined) => {
    if (e == undefined) return Type.none;
    else if (e == Type.type_o) return Type.type_o;
    else return Type.type_x;
}

export default Game;
