import React, {FC, useState} from 'react';
import ReactDOM from 'react-dom';
import Button from '@mui/material/Button';
import {IconButton} from "@mui/material";
import ReplayIcon from '@mui/icons-material/Replay';
import Game from "./components/Game";

const Main:FC = () => {
    const [gameState, setState] = useState({
        reloaded:false
    });
    const ReloadGame =()=>{
        setState({reloaded: true});
        console.log('Game has been reloaded');
        setState({reloaded:false});
    }
    return (
        <div
            style={{
                backgroundColor:'darkblue',
                display: 'flex',
                justifyContent: 'center',
                flexDirection:'column',
                alignItems: 'center',
                width: '100%',
                height: '100%',
                top: '0',
                left: '0',
                position: 'fixed'

            }}
        >
            <IconButton
                style={{
                    backgroundColor:'darkblue',
                    display: 'flex',
                    justifyContent: 'normal',
                    alignItems: 'center',
                    top: '0',
                    left: '0',

                }}
                color={"info"}

            >
                <ReplayIcon fontSize={'large'}/></IconButton>
            <p></p>
            <Game onClick={ReloadGame()}/>
        </div>
    );
};

export default Main;
